import React from "react";
import { Container, Button, Image, Grid, Dropdown, Segment, Label, Form, Checkbox, Icon,Message } from 'semantic-ui-react'
import Layout from "../components/MyLayout"

import * as emailjs from 'emailjs-com'
export default class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {proprio:"",salarie:"",chauffage:"",famille:"",fiscalite:"",
                      nom:"",prenom:"",adresse:"",localite:"",email:"",telephone:""};
    }
    handleChange = (e, { name, value }) => this.setState({ [name]: value });
    
    onSubmit=(data, r) => {
        e.preventDefault();
            this.state.proprio === "" ? this.setState({ errorProprio: { content:"",pointing:null}}) : this.setState({ errorProprio: null })
            this.state.salarie === "" ? this.setState({ errorSalarie: { content:"",pointing:null}}) : this.setState({ errorSalarie: null })
            this.state.chauffage === "" ? this.setState({ errorChauffage: { content: "", pointing:null}}) : this.setState({ errorChauffage: null })
            this.state.famille === "" ? this.setState({ errorFamille: { content: "", pointing:null}}): this.setState({ errorFamille: null })
            this.state.fiscalite === "" ? this.setState({ errorFiscalite: { content: "", pointing:null}}) : this.setState({ errorFiscalite: null })
            this.state.proprio === "" ? alert("Selectionnez un choix") :
            this.state.salarie === "" ? alert("Selectionnez un choix") :
            this.state.chauffage === "" ? alert("Selectionnez un choix"):
            this.state.famille === "" ? alert("Selectionnez un choix"): 
            this.state.fiscalit√© === "" ? alert("Selectionnez un choix") : 
           
            alert(`Thank you for your message from ${data.email}`);
            const templateId = 'eligibilite';
            const serviceID = 'my_Gmail';
            sendFeedback(serviceID, templateId, { from_name: data.name, reply_to: data.email })
            r.target.reset();
            
    }
   
         
    
          
   
    render() {
        const { mobile } = this.props
        return (
            <Layout>
                <Container>
                    <Grid>
                        <Grid.Row centered columns={2}>
                            <Grid.Column style={{ paddingTop: 10, fontFamily: "Comic sans MS" }}>
                                <h1 style={{ fontFamily: "Comic sans MS", color: "green" }}><center>Votre Pompe √† chaleur entierement financ√© par</center></h1>
                                <Image src="img/ecolo/action-logement.png" />
                                <ul>
                                    <li><strong>R√©duisez vos factures de 70% </strong>:La pompe √† chaleur vous permet de faire des
                                            √©conomies consid√©rables <strong>Elle divise la facture de chauffage par 3 !</strong>
                                    </li>

                                    <li><strong>Fini la pollution</strong>:Passez aux √©nergies vertes et propres.</li>

                                    <li><strong> Des professionnels Reconnus Grenelle de l‚ÄôEnvironnement</strong>, vous ferons
                                                    b√©n√©ficier de la Loi de Transition Energ√©tique {this.state.proprio}
                                        </li>
                                </ul>

                                <iframe width="420" height="315"
                                    src="https://www.youtube.com/embed/0m1QWV3vTzo">
                                </iframe>
                                <h2>Nos qualifications</h2>
                                <Image.Group size='tiny'>
                                    <Image src="img/ecolo/engagement-qualite.png" width="auto" height="60" alt="" />
                                    <Image src="img/ecolo/qualipac.png" width="auto" height="60" alt="..." />
                                    <Image src="img/ecolo/garantie-satisfaction.png" width="auto" height="60" alt="..." />
                                </Image.Group>


                            </Grid.Column>

                            <Grid.Column style={{ paddingTop: mobile ? 10 : 20, fontFamily: "Comic sans MS" }}>
                                <Segment color="black">
                                    Dans le cadre de la transition √©cologique, l'√âtat prend en charge le co√ªt de l‚Äôinstallation de votre pompe √† chaleur pour seulement 1‚Ç¨ !<br />
                                    Des milliers de foyers fran√ßais en ont d√©j√† profit√©, pourquoi pas vous ?
                                    <p style={{color:'red'}}>* saisie obligatoire</p>
                                <p style={{ fontFamily: "Comic sans MS", color: "green", fontSize: 30, textAlign: "center" }}>
                                        V√©rifiez votre √©ligibit√©
                                </p>
                                    <Form onSubmit={this.handleSubmit(e)}>
                                        
                                        <Form.Field 
                                                error={this.state.errorProprio}
                                                required
                                                label="Vous Etes" 
                                                placeholder="----"
                                                fluid
                                                options={proprio}
                                                control={Dropdown}
                                                name="proprio"
                                                onChange={this.handleChange}
                                            
                                            /> 
                                        <Message
                                            error
                                            header='Action Forbidden'
                                            content='You can only sign up for an account once with a given e-mail address.'
                                        />
                                            <Form.Field clearable
                                                error={this.state.errorSalarie}
                                                required    
                                                label="Vous etes salari√©"
                                                placeholder="----"
                                                fluid
                                                options={salarie}
                                                control={Dropdown}
                                                name="salarie"
                                                onChange={this.handleChange}
                                            />
                                         
                                            <Form.Field clearable
                                                error={this.state.errorChauffage}
                                                required    
                                                label="Votre type de Chauffage"
                                                placeholder="----"
                                                fluid
                                                options={chauffage}
                                                control={Dropdown}
                                                name="chauffage"
                                                onChange={this.handleChange} 
                                            />
                                            <Form.Field clearable
                                                error={this.state.errorFamille}
                                                required    
                                                label="Nombre de personne vivant dans le foyer"
                                                placeholder="----"
                                                fluid
                                                options={famille}
                                                control={Dropdown}
                                                name="famille"
                                                onChange={this.handleChange}
                                             />
                                            
                                            <Form.Field clearable
                                                error={this.state.errorFiscalite}
                                                required    
                                                label="Revenue fiscal de r√©f√©rence (2020 ou 2019)"
                                                placeholder="----"
                                                fluid
                                                options={fiscalite}
                                                control={Dropdown}
                                                name="fiscalite"
                                                onChange={this.handleChange}
                                             />
                                       
                                        <Form.Group>
                                                <Form.Input 
                                                    required    
                                                    label='Nom' 
                                                    placeholder="Nom"
                                                    width={9}
                                                    name="nom" 
                                                    value={this.state.nom}
                                                    onChange={this.handleChange}
                                                />
                                                <Form.Input 
                                                icon='lock'
                                                iconPosition='left'  
                                                    required    
                                                    label='Pr√©nom' 
                                                    placeholder='Pr√©nom' 
                                                    width={9} 
                                                    name="prenom"
                                                    value={this.state.prenom}
                                                    onChange={this.handleChange}
                                                />
                                        </Form.Group>
                                        <Form.Group>
                                                <Form.Input 
                                                    required 
                                                    icon="adress"
                                                    iconPosition='left'  
                                                    label="Adresse"
                                                    placeholder='Adresse' 
                                                    width={9} 
                                                    name="adresse"
                                                    value={this.state.adresse}
                                                    onChange={this.handleChange}
                                                />
                                                <Form.Input 
                                                    required    
                                                    label='Localit√©' 
                                                    placeholder='Localit√©' 
                                                    width={9}
                                                    name="localite"
                                                    value={this.state.localite}
                                                    onChange={this.handleChange}
                                                 />
                                        </Form.Group>
                                        <Form.Group>
                                            <Form.Input type="email"
                                                required    
                                                label='Email' 
                                                placeholder='contact@gmail.com' 
                                                width={9} 
                                                name="email"
                                                value={this.state.email}
                                                onChange={this.handleChange}
                                            />
                                            <Form.Input 
                                                required    
                                                label='Telephone' 
                                                placeholder='0698611372' 
                                                width={9}
                                                name="telephone"
                                                value={this.state.telephone}
                                                onChange={this.handleChange}
                                                
                                            />
                                        </Form.Group>

                                        <Form.Field>
                                            <Checkbox label="J'autorise R.T.S √† me recontacter pour me communiquer mon statut d'√©ligibilit√©" />
                                        </Form.Field>
                                       
                                        <Form.Button fluid type="submit"color="green" ><h2>V√©rifier mon √©ligibilit√©</h2></Form.Button>
                                    </Form>
                                </Segment>
                            </Grid.Column>
                        </Grid.Row>
                    </Grid>
                </Container>
            </Layout>
        )
    }
}
