import React from "react";
import { Card, Image, Container, Segment,Grid,Item} from 'semantic-ui-react'
import PropTypes from 'prop-types';

export default class Pourquoi extends React.Component {
    constructor(props) {
        super(props);

    }

    render() {
        const { mobile } = this.props


        return (
            <div>
                <Segment>
                    <p style={{
                        fontFamily: "Comic sans MS",
                        textAlign: "center",
                        paddingTop: 30,
                        fontSize: 30,

                    }}>
                        <blockquote>
                            <strong>
                                Action Logement,Ma Prime Renov',CEE:Acteurs Majeurs pour la 
                                TRANSITION ENERGETIQUE</strong>
                        </blockquote></p>
                </Segment>
                <Container fluid>
                    <Segment vertical>
                        <Grid container stackable verticalAlign='center'>
                            <Grid.Row>
                                <Grid.Column floated="left" width={10} style={{ fontFamily: "Comic sans MS" }}>
                                     
                                    <Image src="img/ecolo/action-logement.png" />
                                    

                                    <h1 style={{
                                        fontFamily: "comic sans MS",
                                        textAlign: "center", color: "green",
                                        fontSize: mobile ? 25 : 35
                                    }}>
                                        <u>Comment l'obtenir</u></h1>

                                     

                                </Grid.Column>
                                </Grid.Row>
                                </Grid>
                                </Segment>
                    </Container>

            </div>

        )
    }
}
