    }
         
    dataContact(){ 
            var url = '/api/dataContact'
            
            var dataContact = {
            proprio: this.state.proprio,
            salarie: this.state.salarie,
            chauffage: this.state.chauffage,

        }
        
        fetch(url, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ dataContact })
        })
            .then(resp => {
                const status = resp.status
                if (status !== 200) {
                    this.setState({ open: false })
                    throw new Exception("Errreur")
                }
                return resp.json()
            })
            .then(data => {
                this.setState({ open: true })
                this.setState({ data: data.reponse })
                this.setState({ proprio: "", salarie: "", chauffage: "", error: false })

            })
