                                            content='You can only sign up for an account once with a given e-mail address.'
                                        />
                                            <Form.Field clearable
                                                error={this.state.errorSalarie}
                                                required    
                                                label="Vous etes salariÃ©"
                                                placeholder="----"
                                                fluid
                                                options={salarie}
                                                control={Dropdown}
                                                name="salarie"
                                                onChange={this.handleChange}
                                            />
                                         
                                            <Form.Field clearable
                                                error={this.state.errorChauffage}
                                                required    
                                                label="Votre type de Chauffage"
                                                placeholder="----"
                                                fluid
                                                options={chauffage}
                                                control={Dropdown}
                                                name="chauffage"
                                                onChange={this.handleChange} 
                                            />
                                            <Form.Field clearable
                                                error={this.state.errorFamille}
                                                required    
                                                label="Nombre de personne vivant dans le foyer"
                                                placeholder="----"
                                                fluid
                                                options={famille}
                                                control={Dropdown}
                                                name="famille"
                                                onChange={this.handleChange}
                                             />
                                            
                                            <Form.Field clearable
                                                error={this.state.errorFiscalite}
                                                required    
                                                label="Revenue fiscal de rÃ©fÃ©rence (2020 ou 2019)"
                                                placeholder="----"
                                                fluid
                                                options={fiscalite}
                                                control={Dropdown}
                                                name="fiscalite"
                                                onChange={this.handleChange}
                                             />
                                       
                                        <Form.Group>
                                                <Form.Input 
                                                    required    
                                                    label='Nom' 
                                                    placeholder="Nom"
                                                    width={9}
                                                    name="nom" 
                                                    value={this.state.nom}
                                                    onChange={this.handleChange}
                                                />
                                                <Form.Input 
                                                icon='lock'
                                                iconPosition='left'  
                                                    required    
                                                    label='PrÃ©nom' 
                                                    placeholder='PrÃ©nom' 
                                                    width={9} 
                                                    name="prenom"
                                                    value={this.state.prenom}
                                                    onChange={this.handleChange}
                                                />
                                        </Form.Group>
                                        <Form.Group>
                                                <Form.Input 
                                                    required 
                                                    icon="adress"
