                                                    width={9} 
                                                    name="adresse"
                                                    value={this.state.adresse}
                                                    onChange={this.handleChange}
                                                />
                                                <Form.Input 
                                                    required    
                                                    label='LocalitÃ©' 
                                                    placeholder='LocalitÃ©' 
                                                    width={9}
                                                    name="localite"
                                                    value={this.state.localite}
                                                    onChange={this.handleChange}
                                                 />
                                        </Form.Group>
                                        <Form.Group>
                                            <Form.Input type="email"
                                                required    
                                                label='Email' 
                                                placeholder='contact@gmail.com' 
                                                width={9} 
                                                name="email"
                                                value={this.state.email}
                                                onChange={this.handleChange}
                                            />
                                            <Form.Input 
                                                required    
                                                label='Telephone' 
                                                placeholder='0698611372' 
                                                width={9}
                                                name="telephone"
                                                value={this.state.telephone}
                                                onChange={this.handleChange}
                                                
                                            />
                                        </Form.Group>
                                        <Form.Field>
                                            <Checkbox label="J'autorise R.T.S Ã  me recontacter pour me communiquer mon statut d'Ã©ligibilitÃ©" />
                                        </Form.Field>
                                        <Form.Button disabled={this.state.active} fluid type="submit"color="green" ><h2>VÃ©rifier mon Ã©ligibilitÃ©</h2></Form.Button>
                                    </Form>
                                </Segment>
                            </Grid.Column>
                        </Grid.Row>
                    </Grid>
                </Container>
            </Layout>
        )
    }
}
